import Debug from 'debug';
import config from '../../config.js';
const {server} = config;
const name = `${server.middlewares.debug.name}::request`;
const debug = Debug(name);
export default (req, res, next) => {
  //debug('cookie', req.cookies);
  if(req.url.indexOf('/api/') === 0) {
    debug('API endpoint', req.url);
  }
  debug('headers', req.url);
  if(req.jwtData) debug('jwtData', req.jwtData);
  next();
};